mapper.select:
select
id, order_form_id, product_id, quantity, product_name, product_price, product_price_discount, level_num,
level_discount, product_price_deal, product_sku, is_refund, commission_rate
from nxtframework.nxt_order_form_product


        and id = #{id}


        and order_form_id = #{orderFormId}


        and product_id = #{productId}


        and quantity = #{quantity}


        and product_name = #{productName}


        and product_price = #{productPrice}


        and product_price_discount = #{productPriceDiscount}


        and level_num = #{levelNum}


        and level_discount = #{levelDiscount}


        and product_price_deal = #{productPriceDeal}


        and product_sku = #{productSku}


        and is_refund = #{isRefund}


        and commission_rate = #{commissionRate}
