mapper.select:
select
id, shopping_cart_id, product_id, quantity, sku, dateline
from nxtframework.nxt_shopping_cart_product


        and id = #{id}


        and shopping_cart_id = #{shoppingCartId}


        and product_id = #{productId}


        and quantity = #{quantity}


        and sku = #{sku}


        and dateline = #{dateline}
